mapper.select:
SELECT T1.id, T1.list_name as listName, T1.list_type as listType, T1.remark, T1.create_user as createUser, T1.update_user as updateUser, (SELECT COUNT(T2.id) FROM application_name_list T2 WHERE T2.name_list_id = T1.id AND T2.delete_time is null) as appCount FROM name_list T1 WHERE T1.org_id = #{params.orgId} AND T1.delete_time is null
AND T1.list_name like CONCAT('%',#{params.name} ,'%')
AND T1.list_type = #{params.type}
ORDER BY T1.update_date DESC limit #{begin},#{num}

SELECT id,list_name
FROM name_list
WHERE list_type=#{listType}
AND org_id=#{orgId}
AND delete_time is null

select 
  count(t1.id) 
from name_list t1 
where 
  t1.delete_time is null 

  and t1.list_name = #{params.name} 

and t1.org_id =  #{params.orgId} 

  and t1.id != #{params.id}

SELECT id
FROM name_list
WHERE list_type=#{listType}
AND org_id=#{orgId}
AND list_name=#{listName}
AND delete_time is null
LIMIT 1
