mapper.select:
select t8.usersId, t8.androidDevicePolicyId, t8.updateDate from (select t1.users_id as usersId, t1.android_device_policy_id as androidDevicePolicyId, t1.update_date as updateDate from android_device_users t1 where t1.delete_time is null
and t1.android_device_policy_id in
#{item}
union all select t3.id as usersId, t4.android_device_policy_id as androidDevicePolicyId, t4.update_date as updateDate from users t3 left join android_device_department t4 on t3.group_id = t4.org_structure_id where t3.delete_time is null and t4.delete_time is null
and t4.android_device_policy_id in
#{item}
union all select t5.id as usersId, t7.android_device_policy_id as androidDevicePolicyId, t7.update_date as updateDate from users t5 left join users_virtual_group t6 on t5.id = t6.user_id left join android_device_virtual_group t7 on t6.virtual_id = t7.virtual_group_id where t5.delete_time is null and t6.delete_time is null and t7.delete_time is null
and t7.android_device_policy_id in
#{item}
) as t8 order by t8.updateDate desc
